import { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";

export default function Expenses(props) {
  const [filterYear, setFilterYear] = useState("2023");

  const filterYearHandler = (year) => {
    setFilterYear(year);
  };

  const filteredExpenses = props.expenses.filter(
    (item) => item.date.getFullYear() === parseInt(filterYear)
  );

  return (
    <Card className="expenses">
      <ExpensesFilter selected={filterYear} onYearFilter={filterYearHandler} />
      {filteredExpenses.length === 0 ? (
        <p class="errorMessage">No expenses for this year</p>
      ) : (
        filteredExpenses.map((filtered_item) => (
          <ExpenseItem
            key={filtered_item.id}
            title={filtered_item.title}
            price={filtered_item.price}
            date={filtered_item.date}
          />
        ))
      )}
    </Card>
  );
}
